from django.urls import pathfrom django.conf.urls import url,includefrom django.contrib.auth import views as auth_viewsfrom django.conf import settingsfrom django.conf.urls.static import staticfrom . import viewsfrom django.contrib.auth.decorators import login_required, permission_requiredfrom equipment.views import (    EquipmentView,    ModelView,    UserLogin)app_name = "equipment"urlpatterns =[    path('', login_required(EquipmentView.as_view(template_name="index.html")), name='equipment'),    path('newmodel', login_required(ModelView.as_view(template_name="model.html")), name='newmodel'),    path('model', login_required(views.savemodel), name='savemodel'),    path('login', login_required(UserLogin.as_view(template_name="user_login.html")), name='login'),    path('images', login_required(views.showimage), name='showimage'),     path('staff/', include("users.urls")),    ] + static(settings.MEDIA_URL, document_root=settings.MEDIA_ROOT)        