from django.urls import pathfrom django.conf.urls import url,includefrom django.contrib.auth import views as auth_viewsfrom datetime import datetimefrom . import viewsfrom django.conf import settingsfrom django.conf.urls.static import staticfrom django.contrib.auth.decorators import login_required, permission_requiredfrom locations.views import (    LocationView,    UserLogin)app_name = "locations"urlpatterns =[    path('', login_required(LocationView.as_view(template_name="index.html")), name='location'),    path("site/<slug:location_id>/", views.site, name ="site"),    path('login', login_required(UserLogin.as_view(template_name="user_login.html")), name='login'),    path("searchsite", views.searchsite, name ="searchsite"),    path('staff/', include("users.urls")),    ]+ static(settings.MEDIA_URL, document_root=settings.MEDIA_ROOT)    